--
-- Структура таблицы `wp_posts`
--

CREATE TABLE `wp_posts` (
  `ID` bigint(20) UNSIGNED NOT NULL,
  `post_author` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_parent` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0'
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

--
-- Дамп данных таблицы `wp_posts`
--

INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-11-10 05:15:48', '2017-11-10 05:15:48', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2017-11-10 05:15:48', '2017-11-10 05:15:48', '', 0, 'http://zoopatroopa.com/?p=1', 0, 'post', '', 1),
(2, 1, '2017-11-10 05:15:48', '2017-11-10 05:15:48', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://zoopatroopa.com/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2017-11-10 05:15:48', '2017-11-10 05:15:48', '', 0, 'http://zoopatroopa.com/?page_id=2', 0, 'page', '', 0),
(3, 1, '2017-11-10 05:16:06', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2017-11-10 05:16:06', '0000-00-00 00:00:00', '', 0, 'http://zoopatroopa.com/?p=3', 0, 'post', '', 0),
(4, 5, '2017-11-10 06:31:36', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2017-11-10 06:31:36', '0000-00-00 00:00:00', '', 0, 'http://zoopatroopa.com/?p=4', 0, 'post', '', 0),
(5, 5, '2017-11-10 06:53:58', '2017-11-10 06:53:58', '<div><a href=\"https://news.mail.ru/incident/31593486/?frommail=1\">Link to the source</a></div><br><figure><img src=\"https://retina.news.mail.ru/prev780x440/pic/83/6b/image31593486_813a7aa7c3a077b482da27e9633a8a5e.jpg\"></figure><p><em>ÐœÐ¾ÑÐºÐ²Ð°. 10 Ð½Ð¾ÑÐ±Ñ€Ñ. INTERFAX.RU</em> — Ð¢ÐµÐ»Ð¾ Ð¶ÐµÐ½Ñ‰Ð¸Ð½Ñ‹ Ð¾Ð±Ð½Ð°Ñ€ÑƒÐ¶ÐµÐ½Ð¾ Ð² Ð¿Ð¾Ð´Ð²Ð°Ð»Ðµ Ð¾Ð±Ñ€ÑƒÑˆÐ¸Ð²ÑˆÐµÐ³Ð¾ÑÑ Ð½Ð°ÐºÐ°Ð½ÑƒÐ½Ðµ Ð¿Ð¾Ð´ÑŠÐµÐ·Ð´Ð° Ð¶Ð¸Ð»Ð¾Ð³Ð¾ Ð´Ð¾Ð¼Ð° Ð² Ð˜Ð¶ÐµÐ²ÑÐºÐµ, ÑÐ¾Ð¾Ð±Ñ‰Ð¸Ð» Ð°Ð³ÐµÐ½Ñ‚ÑÑ‚Ð²Ñƒ «Ð˜Ð½Ñ‚ÐµÑ€Ñ„Ð°ÐºÑ» Ð¸ÑÑ‚Ð¾Ñ‡Ð½Ð¸Ðº Ð² ÑÐºÑÑ‚Ñ€ÐµÐ½Ð½Ñ‹Ñ… ÑÐ»ÑƒÐ¶Ð±Ð°Ñ….</p><p>«Ð¢ÐµÐ»Ð¾ Ð¶ÐµÐ½Ñ‰Ð¸Ð½Ñ‹ Ð½Ð°Ð¹Ð´ÐµÐ½Ð¾ Ð² Ð¿Ð¾Ð´Ð²Ð°Ð»Ðµ», — ÑÐºÐ°Ð·Ð°Ð» ÑÐ¾Ð±ÐµÑÐµÐ´Ð½Ð¸Ðº Ð°Ð³ÐµÐ½Ñ‚ÑÑ‚Ð²Ð°.</p><p>«Ð¡ÐµÐ¹Ñ‡Ð°Ñ, Ðº ÑÐ¾Ð¶Ð°Ð»ÐµÐ½Ð¸ÑŽ, Ð¾Ð±Ð½Ð°Ñ€ÑƒÐ¶Ð¸Ð»Ð¸ Ð¶ÐµÐ½Ñ‰Ð¸Ð½Ñƒ Ð±ÐµÐ· Ð¿Ñ€Ð¸Ð·Ð½Ð°ÐºÐ¾Ð² Ð¶Ð¸Ð·Ð½Ð¸», — ÑÐºÐ°Ð·Ð°Ð»Ð° Ð¾Ð½Ð°.</p><p>Ð¢Ð°ÐºÐ¸Ð¼ Ð¾Ð±Ñ€Ð°Ð·Ð¾Ð¼, Ñ‡Ð¸ÑÐ»Ð¾ Ð¶ÐµÑ€Ñ‚Ð² Ð¾Ð±Ñ€ÑƒÑˆÐµÐ½Ð¸Ñ Ð²Ð¾Ð·Ñ€Ð¾ÑÐ»Ð¾ Ð´Ð¾ ÑˆÐµÑÑ‚Ð¸ Ñ‡ÐµÐ»Ð¾Ð²ÐµÐº.</p><figure></figure><p>1 Ñ‡Ð°Ñ Ð½Ð°Ð·Ð°Ð´<a href=\"http://www.newstube.ru/?utm_source=mail&amp;utm_medium=referral&amp;utm_content=newstube&amp;utm_campaign=copy\">© ÐÑŒÑŽÑÑ‚ÑŽÐ±</a></p>', 'Число погибших при обрушении дома в Ижевске возросло до шести', '', 'trash', 'open', 'open', '', '%d1%87%d0%b8%d1%81%d0%bb%d0%be-%d0%bf%d0%be%d0%b3%d0%b8%d0%b1%d1%88%d0%b8%d1%85-%d0%bf%d1%80%d0%b8-%d0%be%d0%b1%d1%80%d1%83%d1%88%d0%b5%d0%bd%d0%b8%d0%b8-%d0%b4%d0%be%d0%bc%d0%b0-%d0%b2__trashed', '', '', '2017-11-10 06:55:27', '2017-11-10 06:55:27', '', 0, 'http://zoopatroopa.com/2017/11/10/%d1%87%d0%b8%d1%81%d0%bb%d0%be-%d0%bf%d0%be%d0%b3%d0%b8%d0%b1%d1%88%d0%b8%d1%85-%d0%bf%d1%80%d0%b8-%d0%be%d0%b1%d1%80%d1%83%d1%88%d0%b5%d0%bd%d0%b8%d0%b8-%d0%b4%d0%be%d0%bc%d0%b0-%d0%b2-%d0%b8%d0%b6/', 0, 'post', '', 0),
(6, 5, '2017-11-10 06:54:51', '2017-11-10 06:54:51', '<div><a href=\"http://edition.cnn.com/2017/11/09/politics/stephen-miller-interviewed-special-counsel-russia-investigation/index.html\">Link to the source</a></div><br><figure><img alt=\"Mueller interviews top WH aide Stephen Miller\" src=\"http://cdn.cnn.com/cnnnext/dam/assets/171109160533-02-stephen-miller-file-exlarge-169.jpg\"></figure><p>LOLOLOLOLOLOLOLOLOLOLOLOLOLOLOLOLOOLWashington (CNN)White House senior policy adviser Stephen Miller has been interviewed as part of special counsel Robert Mueller\'s Russia probe, according to sources familiar with the investigation.</p><p>The interview brings the special counsel investigation into President Donald Trump\'s inner circle in the White House. Miller is the highest-level aide still working at the White House known to have talked to investigators.</p><p>Miller\'s role in the firing of FBI Director James Comey was among the topics discussed during the interview as part of the probe into possible obstruction of justice, according to one of the sources.</p><figure><a href=\"http://edition.cnn.com/2017/11/09/politics/keith-schiller-russian-offer-women-2013-moscow/index.html\"><img alt=\"Ex-Trump security chief testifies he rejected 2013 Russian offer of women for Trump in Moscow\" src=\"http://cdn.cnn.com/cnnnext/dam/assets/171109145627-keith-schiller-06-23-2017-medium-plus-169.jpg\"></a><figcaption>Ex-Trump security chief testifies he rejected 2013 Russian offer of women for Trump in Moscow</figcaption></figure><p>Special counsel investigators have also shown interest in talking to attendees of a March 2016 meeting where foreign policy adviser George Papadopoulos said that he could arrange a meeting between Trump and Russian President Vladimir Putin through his connections. Miller was also at the meeting, according to a source familiar with the meeting.</p><p>Earlier this year, Miller assisted Trump in writing a memo that explained why Trump planned to fire Comey, according to sources familiar with the matter. Eventually that memo was scrapped because of opposition by White House counsel Don McGahn, who said its contents were problematic, <a href=\"https://www.nytimes.com/2017/09/01/us/politics/trump-comey-firing-letter.html?_r=0\">according to The New York Times</a>. The Comey dismissal letter -- drafted during a May weekend at Trump\'s golf club in Bedminster, New Jersey -- has also drawn interest from the Mueller team. Sources tell CNN that White House senior adviser Jared Kushner, who was also in New Jersey that weekend, did not oppose the decision to fire Comey. <a href=\"http://www.cnn.com/2017/11/02/politics/jared-kushner-robert-mueller-documents-russia-investigation/index.html\">CNN has reported</a> the special counsel\'s team is asking questions in interviews with witnesses about Kushner\'s role in Comey\'s firing.</p><figure><a href=\"http://edition.cnn.com/2017/11/08/politics/joseph-mifsud-trump-russia-investigation/index.html\"><img alt=\"Academic at heart of Clinton \'dirt\' claim vanishes, leaving trail of questions\" src=\"http://cdn.cnn.com/cnnnext/dam/assets/171031143307-01-professor-joseph-mifsud-medium-plus-169.jpg\"></a><figcaption>Academic at heart of Clinton \'dirt\' claim vanishes, leaving trail of questions</figcaption></figure><p>The Times reported in September that the Justice Department had turned over a copy of the letter, which was never sent, to special counsel Robert Mueller. That memo, according to a source, was very similar to a letter written by Deputy Attorney General Rod Rosenstein that was cited as the reason for firing Comey. Rosenstein\'s letter criticized Comey\'s handling of the investigation into Hillary Clinton\'s private email server.</p><p>But just days after the firing, Trump said he considered the Russia probe in his decision to fire Comey.</p><p>\"In fact when I decided to just do it, I said to myself, I said \'you know, this Russia thing with Trump and Russia is a made-up story, it\'s an excuse by the Democrats for having lost an election that they should have won\',\" Trump said about his decision to fire Comey in a May interview with NBC News.</p><figure><a href=\"http://edition.cnn.com/2017/11/08/politics/michael-flynn-son-special-counsel-russia-investigation/index.html\"><img alt=\"Flynn worries about son in special counsel probe\" src=\"http://cdn.cnn.com/cnnnext/dam/assets/171108182508-flynn-flynn-jr-medium-plus-169.jpg\"></a><figcaption>Flynn worries about son in special counsel probe</figcaption></figure><p>Miller was also an early member of Trump\'s campaign staff, leaving his role as Sessions\' communications director in the Senate to join Trump in January 2016.</p><p>Mueller\'s team has also talked to key former aides including former White House chief of staff Reince Preibus and former White House press secretary Sean Spicer. They have also interviewed National Security Council chief of staff Keith Kellogg. CNN has previously reported Mueller is also seeking to interview other White House staff including McGahn, communications director Hope Hicks and Kushner aide Josh Raffell.</p><p>The special counsel\'s office declined to comment. Miller did not respond to a request for comment.</p><p>CNN\'s Mary Kay Mallonee and Brian Rokus contributed to this report.</p>', 'Mueller interviews top White House aide', '', 'publish', 'open', 'open', '', 'mueller-interviews-top-white-house-aide', '', '', '2017-11-10 06:54:51', '2017-11-10 06:54:51', '', 0, 'http://zoopatroopa.com/2017/11/10/mueller-interviews-top-white-house-aide/', 0, 'post', '', 0),
(7, 5, '2017-11-10 06:55:27', '2017-11-10 06:55:27', '<div><a href=\"https://news.mail.ru/incident/31593486/?frommail=1\">Link to the source</a></div><br><figure><img src=\"https://retina.news.mail.ru/prev780x440/pic/83/6b/image31593486_813a7aa7c3a077b482da27e9633a8a5e.jpg\"></figure><p><em>ÐœÐ¾ÑÐºÐ²Ð°. 10 Ð½Ð¾ÑÐ±Ñ€Ñ. INTERFAX.RU</em> — Ð¢ÐµÐ»Ð¾ Ð¶ÐµÐ½Ñ‰Ð¸Ð½Ñ‹ Ð¾Ð±Ð½Ð°Ñ€ÑƒÐ¶ÐµÐ½Ð¾ Ð² Ð¿Ð¾Ð´Ð²Ð°Ð»Ðµ Ð¾Ð±Ñ€ÑƒÑˆÐ¸Ð²ÑˆÐµÐ³Ð¾ÑÑ Ð½Ð°ÐºÐ°Ð½ÑƒÐ½Ðµ Ð¿Ð¾Ð´ÑŠÐµÐ·Ð´Ð° Ð¶Ð¸Ð»Ð¾Ð³Ð¾ Ð´Ð¾Ð¼Ð° Ð² Ð˜Ð¶ÐµÐ²ÑÐºÐµ, ÑÐ¾Ð¾Ð±Ñ‰Ð¸Ð» Ð°Ð³ÐµÐ½Ñ‚ÑÑ‚Ð²Ñƒ «Ð˜Ð½Ñ‚ÐµÑ€Ñ„Ð°ÐºÑ» Ð¸ÑÑ‚Ð¾Ñ‡Ð½Ð¸Ðº Ð² ÑÐºÑÑ‚Ñ€ÐµÐ½Ð½Ñ‹Ñ… ÑÐ»ÑƒÐ¶Ð±Ð°Ñ….</p><p>«Ð¢ÐµÐ»Ð¾ Ð¶ÐµÐ½Ñ‰Ð¸Ð½Ñ‹ Ð½Ð°Ð¹Ð´ÐµÐ½Ð¾ Ð² Ð¿Ð¾Ð´Ð²Ð°Ð»Ðµ», — ÑÐºÐ°Ð·Ð°Ð» ÑÐ¾Ð±ÐµÑÐµÐ´Ð½Ð¸Ðº Ð°Ð³ÐµÐ½Ñ‚ÑÑ‚Ð²Ð°.</p><p>«Ð¡ÐµÐ¹Ñ‡Ð°Ñ, Ðº ÑÐ¾Ð¶Ð°Ð»ÐµÐ½Ð¸ÑŽ, Ð¾Ð±Ð½Ð°Ñ€ÑƒÐ¶Ð¸Ð»Ð¸ Ð¶ÐµÐ½Ñ‰Ð¸Ð½Ñƒ Ð±ÐµÐ· Ð¿Ñ€Ð¸Ð·Ð½Ð°ÐºÐ¾Ð² Ð¶Ð¸Ð·Ð½Ð¸», — ÑÐºÐ°Ð·Ð°Ð»Ð° Ð¾Ð½Ð°.</p><p>Ð¢Ð°ÐºÐ¸Ð¼ Ð¾Ð±Ñ€Ð°Ð·Ð¾Ð¼, Ñ‡Ð¸ÑÐ»Ð¾ Ð¶ÐµÑ€Ñ‚Ð² Ð¾Ð±Ñ€ÑƒÑˆÐµÐ½Ð¸Ñ Ð²Ð¾Ð·Ñ€Ð¾ÑÐ»Ð¾ Ð´Ð¾ ÑˆÐµÑÑ‚Ð¸ Ñ‡ÐµÐ»Ð¾Ð²ÐµÐº.</p><figure></figure><p>1 Ñ‡Ð°Ñ Ð½Ð°Ð·Ð°Ð´<a href=\"http://www.newstube.ru/?utm_source=mail&amp;utm_medium=referral&amp;utm_content=newstube&amp;utm_campaign=copy\">© ÐÑŒÑŽÑÑ‚ÑŽÐ±</a></p>', 'Число погибших при обрушении дома в Ижевске возросло до шести', '', 'inherit', 'closed', 'closed', '', '5-revision-v1', '', '', '2017-11-10 06:55:27', '2017-11-10 06:55:27', '', 5, 'http://zoopatroopa.com/2017/11/10/5-revision-v1/', 0, 'revision', '', 0),
(8, 5, '2017-11-10 18:54:05', '2017-11-10 18:54:05', '<div><a href=\"http://money.cnn.com/2017/11/10/media/att-trump-antitrust-case/index.html\">Link to the source</a></div><br><figure><a href=\"http://get.adobe.com/flashplayer/\"><img alt=\"\" src=\"http://i2.cdn.turner.com/money/.element/img/4.0/video/no_flash/620x384_no_flash.gif\"></a><figcaption>AT&amp;T CEO speaks out on the Time Warner deal</figcaption></figure><h2>The Trump administration\'s showdown with AT&amp;T could turn into a legal fight unlike anything corporate America has seen in four decades.</h2><p>Justice Department and AT&amp;T sources told CNNMoney this week that AT&amp;T would either need to buy Time Warner without its Turner unit, which includes CNN, or give up DirecTV, which it already owns, to gain the government\'s blessing.</p><p>AT&amp;T CEO <a href=\"http://money.cnn.com/2017/11/09/media/att-ceo-randall-stephenson-speaks/index.html?iid=EL\">Randall Stephenson said</a> at the Dealbook conference in New York on Thursday that selling CNN is not in the cards, and he\'s ready to go to trial.</p><p>Virtually all mergers are either approved or scuttled outside of court, but that\'s especially true for the kind of deal AT&amp;T is seeking.</p><p>Its proposed deal with Time Warner is a so-called vertical acquisition, because Time Warner would add a new line of business to AT&amp;T: AT&amp;T (<a href=\"http://money.cnn.com/quote/quote.html?symb=T&amp;source=story_quote_link\">T</a>, <a href=\"http://money.cnn.com/technology/tech30/index.html?iid=EL\">Tech30</a>) sells internet, phone and video services, and Time Warner (<a href=\"http://money.cnn.com/quote/quote.html?symb=TWX&amp;source=story_quote_link\">TWX</a>) makes content. The companies do not compete with one another. Because vertical mergers typically don\'t reduce competition, U.S. regulators very rarely fight to stop them.</p><p>If the dispute does end up in court, it could be the first vertical acquisition case that the U.S. government has fought in court all the way to conclusion since 1979, according to Steven Salop, a professor at Georgetown Law.</p><p>Why do so few mergers end up in court? The legal process is long and expensive. The burden of proof favors the government. And antitrust sentiment has shifted in favor of companies.</p><p><strong>Time is of the essence.</strong> Even a case that\'s on a fast track can take six months to complete, and an appeal could take six more months.</p><p>All those legal bills are expensive on their own. But the companies also have to enter a holding pattern, unable to adjust their strategies as they wait to see whether they will combine or go their separate ways.</p><p>In many ways, time can be the enemy of the deal. A lot can change in the span of a lengthy regulatory battle that can make a deal less appetizing, including shifting markets, stock prices and economic conditions.</p><p><strong>Low burden of proof. </strong>Unlike in most other countries, the government does not have the power to block a deal outright -- it has to sue to block it.</p><p>If the government does challenge a merger, it typically brings a lawsuit under Section 7 of the century-old Clayton Act. To win its case, the government only needs to prove that a merger \"may\" harm competition, according to Craig Wildfang, antitrust attorney at Robins Kaplan.</p><p>That\'s why the vast majority of companies that regulators challenge either drop their merger bids or settle outside of court. Regulators will often attach stipulations called consent judgments that force companies to divest certain assets or agree to pro-competitive behaviors.</p><p>Those settlements have to be approved by a judge, but they\'re almost always granted.</p><p><strong>Changing sentiment. </strong>In the 1970s case, the government sued to block the nation\'s largest truck trailer company from buying the biggest manufacturer of truck wheels. Though they weren\'t direct competitors, the FTC argued that the combined company could illegally harm the truck wheel market. The government lost on appeal, and the deal went through.</p><p>Shortly after that trial, the tide turned in favor of merging companies. Under President Reagan, the government appointed antitrust lawyers who favored less government intervention in corporate acquisitions.</p><p>\"Since the 1980s and \'90s, the judicial attitude has been to let free markets flourish,\" said Sam Miller, a professor at UC Hastings and the prosecutor who led the government\'s first antitrust case against Microsoft two decades ago.</p><p>That sentiment continued <a href=\"http://money.cnn.com/2016/04/08/news/obama-mergers-antitrust/index.html?iid=EL\">even throughout President Obama\'s administration</a>, which blocked only slightly more mergers than his Republican predecessor, George W. Bush.</p><p>For example, Obama administration regulators sued to block just <a href=\"https://www.ftc.gov/news-events/press-releases/2017/10/ftc-doj-issue-fiscal-year-2016-hart-scott-rodino-premerger\">47 of the nation\'s 1,832 mergers last year</a>. Only three of those cases ended up going to trial, but all of those were different from AT&amp;T/Time Warner -- horizontal mergers that would have taken a competitor out of the marketplace.</p><p>The Justice Department won all three of those cases this year, including the scuttling of proposed health insurance mega-mergers between Anthem and Cigna and between Aetna and Humana.</p><p><strong>Where does that leave AT&amp;T and Time Warner? </strong>Stephenson said that the companies are still negotiating with the Justice Department, and the deal could ultimately be settled out of court.</p><p>But if the case does go to trial, it could be a long, drawn out process that could take years to complete.</p>', 'AT&amp;T\'s battle with Trump administration could enter rare territory', '', 'publish', 'open', 'open', '', 'atts-battle-with-trump-administration-could-enter-rare-territory', '', '', '2017-11-10 18:54:05', '2017-11-10 18:54:05', '', 0, 'http://zoopatroopa.com/2017/11/10/atts-battle-with-trump-administration-could-enter-rare-territory/', 0, 'post', '', 0),
(9, 5, '2017-11-11 12:22:17', '2017-11-11 12:22:17', '<div><a href=\"http://edition.cnn.com/2017/11/10/politics/moore-unholy-excuses/index.html\">Link to the source</a></div><br><figure><img alt=\"Reporter tells why Moore\'s accusers spoke up\" src=\"http://cdn.cnn.com/cnnnext/dam/assets/171109133017-04-roy-moore-lead-image-exlarge-169.jpg\"></figure><p>(CNN)Almost every religion asks a version of the question, \"What would Jesus do?\" Buddhists look to the Buddha, Muslims to the Prophet Mohammed, Sikhs to Guru Nanak, observant Jews to Moses, and the list goes on.</p><p>Emulating holy men and women from the distant past is hard -- what would the Buddha do with Facebook? -- but the effort has helped faiths keep their founders\' missions alive, even as they amble across centuries and continents. Still, there is a shadow side to this devotion: the use, or misuse, of sacred stories to condone bad behavior.</p><p>The latest and perhaps oddest example came this week, when an Alabama official i<a href=\"http://www.cnn.com/2017/11/10/politics/roy-moore-joseph-and-mary/index.html\">nvoked Christianity\'s Holy Family</a> to defend Senate GOP candidate Roy Moore from accusations that he sexually assaulted a 14-year-old girl decades ago.</p><p>\"Take Joseph and Mary,\" Alabama State Auditor Jim Zeigler told the Washington Examiner. \"Mary was a teenager and Joseph was an adult carpenter. They became parents of Jesus. There\'s just nothing immoral or illegal here. Maybe just a little bit unusual.\"</p><p>Moore, for his part, has vigorously denied the accusations. In keeping with his hardcore Christian image, the Senate candidate said he is engaged in a \"spiritual battle\" with \"those who want to silence our message.\"</p><p>But few Christians have risen to voice support for Zeiglers\' interpretation of Scripture. In fact, quite the opposite.</p><p>\"As Christians, this should provoke anger,\" said Stetzer, who heads the Billy Graham Center at Wheaton College in Illinois.</p><p>\"We should be angered, first, that politicians think they can lie to us so easily by appealing to biblical language and characters. Second, that we so easily fall for such tactics. For the past decade, evangelicals have been easy marks, and I hope that people won\'t fall for these things.\"</p><p>For what it\'s worth, the Bible doesn\'t tell us how old Mary and Joseph were. The gospels refer to Mary as a \"parthenos,\" which can be translated as \"young woman\" or \"virgin,\" said the Rev. James Martin, a Jesuit priest and author of a recent book on the life of Jesus.</p><p>In biblical art, Joseph is often depicted as elderly and Mary as maidenly, but the purpose is to desexualize their relationship, Martin said, not to normalize liaisons between older men and younger women.</p><p>\"Using the relationship of Mary and Joseph to, in any way, excuse or legitimize the sexual abuse or sexual harassment of a minor, or anyone, is monstrous,\" <a href=\"https://twitter.com/JamesMartinSJ/status/929076794896076800\">Martin tweeted. </a></p><p>Zeigler\'s comparison is bizarre for another reason, Christians said. Joseph didn\'t prey on Mary, according to the Bible. He protected her.</p><p>As the Nativity story recounts every Advent, Jesus was not conceived through relations between Joseph and Mary. In fact, when Joseph heard that Mary was pregnant, he considered divorce, Scripture says, until an angel told him about Jesus\' divine father. After that, Joseph remained betrothed to Mary and raised Jesus as a son.</p><p>Friday, as news spread about the allegations against Moore, another of his allies compared the Senate candidate not to Joseph and Mary but to Jesus himself.</p><p>Jerry Moore, Roy Moore\'s brother,<a href=\"http://www.cnn.com/2017/11/10/politics/jerry-moore-martin-savidge-interview-roy-moore-sexual-misconduct/index.html\"> told CNN\'s Martin Savidge</a> that the women who say Roy Moore pursued them as teenagers decades ago \"will have to answer to God for these false allegations,\" Savidge said.</p><p>Jerry Moore then said that his brother is \"being persecuted ... like Jesus Christ was,\" according to Savidge.</p><p>\"A wall of separation between politicians and sacrilegious comparisons to Christ ought to exist,\" said Philip Wegmann i<a href=\"http://www.washingtonexaminer.com/stop-comparing-roy-moore-to-jesus-christ/article/2640325\">n the Washington Examiner</a>. \"To do otherwise is neither politically safe nor spiritually right.\"</p><p>In some ways, it makes sense for Moore\'s allies to draw on religious examples when defending him. The longtime Alabama Supreme Court judge has made Christianity and the Bible central to his political identity. He has insisted on displaying a monument to the Ten Commandments in the courthouse, even after he was told to remove it, and earned a suspension for refusing to follow the Supreme Court\'s legalization of gay marriage.</p><p>But you don\'t need to be a Bible-thumping Southerner to earn biblical comparisons these days.</p><p>During the 2016 presidential campaign, many of Donald Trump\'s evangelical backers compared the brash and unorthodox businessman to the morally flawed kings in ancient Israel.</p><p>\"God called King David a man after God\'s own heart even though he was an adulterer and a murderer,\" <a href=\"https://www.liberty.edu/champion/2016/03/falwell-speaks/\">said Jerry Falwell Jr.</a>, the president of evangelical Liberty College and a staunch Trump supporter.</p><p>Other evangelicals compared Trump to Saul, the first ruler of the unified kingdoms; to Nehemiah, who built the walls around Jerusalem; and even Esther, who rose from obscurity to become a Persian queen. There grew to be so many biblical analogies that the Washington Post<a href=\"https://www.washingtonpost.com/news/acts-of-faith/wp/2017/01/18/king-donald-guess-the-biblical-kings-trump-has-been-compared-to/?utm_term=.f697f59d99fb\"> made a game of it.</a> (Meanwhile, <a href=\"https://www.prri.org/research/prri-brookings-oct-19-poll-politics-election-clinton-double-digit-lead-trump/\">a growing number of white evangelicals</a> decided to be more accepting of politicians who commit immoral acts in their personal life.)</p><p>What does Christian Scripture say about comparing ourselves to faith\'s founding families? One indication comes in a letter written by St. Paul.</p><p>\"Each one should test their own actions,\" <a href=\"http://biblehub.com/galatians/6-4.htm\">Paul writes in Galatians</a>. \"Then they can take pride in themselves alone, without comparing themselves to someone else, for each one should carry their own load.\"</p>', 'The unholy excuses of Roy Moore\'s allies', '', 'publish', 'open', 'open', '', 'the-unholy-excuses-of-roy-moores-allies', '', '', '2017-11-11 12:22:17', '2017-11-11 12:22:17', '', 0, 'http://zoopatroopa.com/2017/11/11/the-unholy-excuses-of-roy-moores-allies/', 0, 'post', '', 0),
(10, 5, '2017-11-11 12:52:31', '2017-11-11 12:52:31', '<div><a href=\"https://tv.aftonbladet.se/abtv/articles/232216\">Link to the source</a></div><br><p><video controls=\"controls\"></video></p><p>Tony frÃ¥n Australien visar upp sitt genialiska knep</p>', 'Drar fisken i sanden – sen sker det äckliga', '', 'publish', 'open', 'open', '', 'drar-fisken-i-sanden-sen-sker-det-ackliga', '', '', '2017-11-11 12:52:31', '2017-11-11 12:52:31', '', 0, 'http://zoopatroopa.com/2017/11/11/drar-fisken-i-sanden-sen-sker-det-ackliga/', 0, 'post', '', 0),
